package com.example.mysurahappimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.TextViewimport androidx.recyclerview.widget.RecyclerViewclass RecyclerAdapter(private val modelList : ArrayList<Model> ) :    RecyclerView.Adapter<RecyclerAdapter.RecyclerViewHolder>() {    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): RecyclerViewHolder {        val itemView= LayoutInflater.from(parent.context).inflate(R.layout.fragment_surah, parent, false)        return RecyclerViewHolder(itemView)    }    override fun onBindViewHolder(holder: RecyclerViewHolder, position: Int) {        val currentItem = modelList[position]        holder.nameSurah.text = currentItem.nameSurah        holder.numberSurah.text = currentItem.numberSurah        holder.arabicName.text = currentItem.arabicName    }    override fun getItemCount(): Int {        return modelList.size    }    class RecyclerViewHolder(itemView : View) : RecyclerView.ViewHolder(itemView) {        val nameSurah : TextView = itemView.findViewById(R.id.name_surah)        val numberSurah : TextView = itemView.findViewById(R.id.number_surah)        val arabicName: TextView = itemView.findViewById(R.id.arabic_name)    }}